import React,{useState,useEffect} from 'react'

import classes from './AvailableMeals.module.css'

import Card from '../UI/Card';

import MealItem from './MealItem/MealItem';




  const AvailableMeals = ( ) =>{
    const [meals,setMeals] = useState([]);
    const [isLoading,setLoading] = useState(true);
    useEffect(()=>{
      const fetchData =async () => {
        const response = await fetch('https://react-meal-cart-default-rtdb.firebaseio.com/meals.json')
        console.log("third")
        const data = await response.json();
        let loadedMeals=[];
        for(const key in data){
          loadedMeals.push({id:key,name:data[key].name,price:data[key].price,description:data[key].description});
        }

        setMeals(loadedMeals)
      
      }
      fetchData();

    },[setMeals])
    if(isLoading){
      return(<section className={classe}>
        <p>Loading....</p>
      </section>)
    }
      const mealsContent = meals.map(meal =>
      <MealItem 
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}>
      </MealItem>)
      return(<section className={classes.meals}>
          <Card>
          <ul>
            {mealsContent}
         </ul>
      </Card>
      </section>);
  }


  export default AvailableMeals;
