import {} from 'redux'

const countReducer = (state={count:0},action) =>{
    if(action.type === 'INCREMENT'){
        return {count:state.count+1}
    }
    if(action.type === 'DECREMENT'){
        return {count:state.count-1}
    }
    return state;
}


const store = redux.createStore(countReducer)

export default store;
