//first look at the  commented code given below this 
import {createSlice,createStore,configureStore} from '@reduxjs/toolkit'


//creating a slice of reducer functi
const counterSlice = createSlice({
    name:'counter',
    initialState:{count:0,showCounter:true},
    reducers :{
        increment(state){ state.count++},
        decrement(state){ state.count--},
        increase(state,action){ state.count += action.payload},
        toggle(state){state.showCounter = !state.showCounter}

    }
})
 
const initialAuthState = {isAuthenticated:false}
const authSlice = createSlice({
    name:'auth',
    initialState:initialAuthState,
    reducers:{
        login(state){
            state.isAuthenticated = true;

        },
        logout(state){
            state.isAuthenticated = false;

        }
    }

})

//if we have one store
// const store =createStore(counterSlice.reducer)


//if there is multiple reducer slice
// const store = configureStore({
//     reducer: {counter:counterSlice.reducer,auth:authSlice.reducer}
// })

const store = configureStore({
    reducer:{count:counterSlice.reducer,auth:authSlice.reducer}
})

export const counterActions = counterSlice.actions;
export const authActions = counterSlice.actions;

export default store;




