import { useEffect } from 'react';
import useHttp from '../hooks/use-http'
import { getAllQuotes} from '../lib/api'

import QuoteList from '../components/quotes/QuoteList'
import LoadingSpinner from '../components/UI/LoadingSpinner';
const DUMMY_QUOTES = [
    {id:'q1',author:'Afsal',text:'Everything will be alright'},
    {id:'q2',author:'Ashiq',text:'Money is everything.'},
    {id:'q3',author:'Thalapathy',text:'Kill them with your smile,burry them with your success.'}
]

const AllQuotes = () =>{
    const {sendRequest,data:loadedQuotes,error,status} = useHttp(getAllQuotes,true)
    useEffect(()=>{
        sendRequest()

    },[sendRequest])

    if(status === 'pending'){
        return(<div className='centered'>
            <LoadingSpinner></LoadingSpinner>
        </div>);
    }
    if(error)

    return(<QuoteList quotes={DUMMY_QUOTES}></QuoteList>);

}

export default AllQuotes;
