import React from 'react';
import {Route,useParams,L} from 'react-router-dom'
 import Comments from '../components/comments/Comments'
 import HighlightedQuotes from '../components/quotes/HighlightedQuote'

 const DUMMY_QUOTES = [
    {id:'q1',author:'Afsal',text:'Everything will be alright'},
    {id:'q2',author:'Ashiq',text:'Money is everything.'},
    {id:'q3',author:'Thalapathy',text:'Kill them with your smile,burry them with your success.'}
]

const QuotesDetails = () =>{
    const params = useParams()
    const quote = DUMMY_QUOTES.find(quote=>quote.id === params.id)
    if(!quote){
        return (<h1>Not Quotes Found</h1>);
    }

    return(<React.Fragment>
        <HighlightedQuotes text={quote.text} author={quote.author}></HighlightedQuotes>
        <Route path={`/quotes/${params.id}/comments`}>
            <Comments></Comments>

        </Route>
        </React.Fragment>);

}

export default QuotesDetails;
