import React from 'react';
import {Route,useParams,Link,useRouteMatch} from 'react-router-dom'
 import Comments from '../components/comments/Comments'
 import HighlightedQuotes from '../components/quotes/HighlightedQuote'

 const DUMMY_QUOTES = [
    {id:'q1',author:'Afsal',text:'Everything will be alright'},
    {id:'q2',author:'Ashiq',text:'Money is everything.'},
    {id:'q3',author:'Thalapathy',text:'Kill them with your smile,burry them with your success.'}
]

const QuotesDetails = () =>{
    const match = useRouteMatch()
    const params = useParams()
    const quote = DUMMY_QUOTES.find(quote=>quote.id === params.id)
    if(!quote){
        return (<h1>Not Quotes Found</h1>);
    }
    //match.path contain quotes/:id
    //match.url contain quotes/q2
    //ie;-whenever we change the main path it is inconvinient to chan

    return(<React.Fragment>
        <HighlightedQuotes text={quote.text} author={quote.author}></HighlightedQuotes>
        <Route path={`${match.path}/` } exact>
        <div className='centered'>
            <Link className='btn--flat' to={`${match.url}/comments`}>Load Comments</Link>
        </div>
        </Route>
    
        <Route path={`${match.path}/comments`}>
            <Comments></Comments>

        </Route>
        </React.Fragment>);

}

export default QuotesDetails;
