import React,{useEffect} from 'react'
import { useSelector,useDispatch} from 'react-redux';

import { uiActions } from './store/ui-slice';

import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import Notification from './components/UI/Notification';

function App() {
  const showCart = useSelector((state) => state.ui.cartIsVisible);
  const showNot = useSelector((state)=>state.ui.notifivation)
  const cart = useSelector(state => state.cart)
  const dispatch = useDispatch()

  useEffect(()=>{
    const sendRequest = async()=>{
      sendRequest.catch(err=>{
        dispatch(uiActions.setNotification({title:'Sending',
        message:'Sending request...',
        status:'pending'
        }))
      })
    const response=await fetch('https://react-request-test-default-rtdb.firebaseio.com/cart.json',{
      //instead of adding overwrite the current cart
      method:'PUT',
      body:JSON.stringify(cart)
    })
    if(!response.ok){
      throw new E
    }

  }
  sendRequest.catch(err=>{
    sendRequest.catch(err=>{
      dispatch(uiActions.setNotification({title:'Failed',
      message:err.message,
      status:'error'
      }))
    })
  })





},[cart,dispatch])


  return (
    <React.Fragment>
     {showNot && <Notification></Notification>}
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
    </React.Fragment>
  );
}

export default App;
